            "event": [
                {
                    "listen": "test",
                    "script": {
                        "exec": [
                            "pm.test(\"Status code is 200\", function () {",
                            "    pm.response.to.have.status(200);",
                            "});"
                        ],
                        "type": "text/javascript"
                    }
                }
            ],
            "request": {
                "method": "GET",
                "header": [],
                "url": {
                    "raw": "{{base_url}}/books",
                    "host": [
                        "{{base_url}}"
                    ],
                    "path": [
                        "books"
                    ]
                },
                "description": "This is a GET request and it is used to \"get\" data from an endpoint. There is no request body for a GET request, but you can use query parameters to help specify the resource you want data on (e.g., in this request, we have `id=1`).\n\nA successful GET response will have a `200 OK` status, and should include some kind of response body - for example, HTML web content or JSON data."
            },
            "response": []
        },
        {
            "name": "Get One Boook",
            "request": {
                "method": "GET",
                "header": [],
                "url": {
                    "raw": "{{base_url}}/books/1",
                    "host": [
                        "{{base_url}}"
                    ],
                    "path": [
                        "books",
                        "1"
                    ]
                }
            },
            "response": []
        },
        {
            "name": "Get All Pages From a Book",
            "request": {
                "method": "GET",
                "header": [],
                "url": {
                    "raw": "{{base_url}}/books/1/pages",
                    "host": [
                        "{{base_url}}"
                    ],
                    "path": [
                        "books",
                        "1",
                        "pages"
                    ]
                }
            },
            "response": []
        },
        {
            "name": "Get All Pages from a Book In a Specific Format",
            "request": {
                "method": "GET",
                "header": [],
                "url": {
                    "raw": "{{base_url}}/books/1/pages/html",
                    "host": [
                        "{{base_url}}"
                    ],
                    "path": [
                        "books",
                        "1",
                        "pages",
                        "html"
                    ]
                }
            },
            "response": []
        },
        {
            "name": "Get One Page From a Book",
            "request": {
                "method": "GET",
                "header": [],
                "url": {
                    "raw": "{{base_url}}/books/1/pages/1",
                    "host": [
                        "{{base_url}}"
                    ],
                    "path": [
                        "books",
                        "1",
                        "pages",
                        "1"
                    ]
                }
            },
            "response": []
        },
        {
            "name": "Get One Page From a Book in a specific Page",
            "request": {
                "method": "GET",
                "header": [],
                "url": {
                    "raw": "{{base_url}}/books/1/pages/1/html",
                    "host": [
                        "{{base_url}}"
                    ],
                    "path": [
                        "books",
                        "1",
                        "pages",
                        "1",
                        "html"
                    ]
                }
            },
            "response": []
        }
    ],
    "event": [
        {
            "listen": "prerequest",
            "script": {
                "type": "text/javascript",
                "exec": [
                    ""
                ]
            }
        },
        {
            "listen": "test",
            "script": {
                "type": "text/javascript",
                "exec": [
                    ""
                ]
            }
        }
    ],
    "variable": [
        {
            "key": "id",
            "value": "1"
        },
        {
            "key": "base_url",
            "value": "http://localhost:8000/api"
        }
    ]
